import React, {useState} from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
// import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import { Alert } from '@material-ui/lab';
//import Alert from '@material-ui/lab/Alert';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import loginImg from '../img/login.jpg';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { BrowserRouter, Route, Link, Router,useHistory } from 'react-router-dom';
import axios from 'axios';


const Login = (props) => {
    const classes = useStyles();
    const [formState,setFormState]=useState({});
    const [loading,setIsLoading]=useState(false);
    const [errors,setErrors]=useState(null);
    const [data,setData]=useState({});
    const router=useHistory();
  




    console.log({formState})

    const onChange=({target:{value}},name)=>{
      setFormState({...formState,[name]:value})
      setErrors(null)
    }
    const handleSubmit=(e)=>{
      e.preventDefault()


      if(!formState.email?.length || !formState.Fjalekalimi?.length){
        return false
      }

     const options={

      headers:{
        'ApiKey':"a9d24q8d5cf3-4q5w-48c3-9aq0-1ee7e34d",
      }}
      setIsLoading(true);
      axios.post("https://localhost:5001/api/login",formState, options).then(res=>{
        setData(res.data);
        
        setIsLoading(false);
        router.push("/perditesimi")
      }).catch(err=>{
        setIsLoading(false);
        console.log({err})
        setErrors(err.response?.data.exception_message)
      })
    }

    return(
        <div className={classes.container} variant="outlined">
        <Container component="main" maxWidth="md">
        <Card className={classes.cardRoot}>       
            <CardContent>
      <div className={classes.paper}>
        <Typography component="h1" variant="h4" style={{color: "#3c4858"}}>
          <strong>Kyçu</strong>
        </Typography>
        <form className={classes.form} method="post" 
          onSubmit={handleSubmit}
          style={{textAlign: 'center' }} validate>
          <TextField 
            onChange={(e)=>onChange(e,"email")}            
            style={{width: '65%'}}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email"
            name="email"
            autoComplete="email"
            type="email"
            autoFocus
          />
          <TextField
            onChange={(e)=>onChange(e,"Fjalekalimi")}
            style={{width: '65%'}}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="password"
            label="Password"
            name="password"
            type="password"
            autoComplete="password"
          />

        {errors&&  <Alert style={{width: '65%'}, {paddingLeft: '150px'}} severity="error">{errors||"Email dhe fjalekalimi nuk jane te sakta. Provo perseri!"}
          </Alert>}
          <Button
            onSubmit={handleSubmit}
            type="submit"
            fullWidth
            disabled={loading}
            variant="contained"
            color="primary"
            className={classes.submit}
            style={{width: '40%'}}
          >
            {loading?"Ju lutem prisni!":"Kyçu"}
          </Button>
          <Grid container>

            <Grid item xs>
              <Link href="#" variant="body2" style={{textDecoration: 'none'}}>
                <p style={{fontSize: "18px", marginTop: "10px"}}>Keni harruar fjalëkalimin?</p>
              </Link>
            </Grid>
            
            <Grid item xs>
              <Link to="/signup" variant="body2" style={{textDecoration: 'none'}}>
                <p style={{fontSize: "18px",  marginTop: "10px"}}>Regjistrohu</p>
              </Link>
            </Grid>
          </Grid>
        </form>
      </div>
      </CardContent>
      </Card>
    </Container>
    </div>
    )
}

const useStyles = makeStyles((theme) => ({

  //stilizimi i eres

  /*
    container: {
        backgroundImage: `url(${ubtImage})`,
        backgroundPosition: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        width: '100%',
        height: '100%',
        paddingTop: "100px",
        position: 'fixed',
    },
    paper:{
      paddingTop: theme.spacing(8),
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
    },
    form: {
      width: '100%',
      marginTop: theme.spacing(1),
    },
    submit: {
      margin: theme.spacing(3, 0, 2),
      fontSize: "18px",
      paddingTop: "18px",
      paddingBottom: "18px"
    },
    formControl: {
    marginTop: theme.spacing(2),
    minWidth: 'calc(100%)',
    maxWidth: 300,
  },
  */

  //Stilizimi i edonit mos e fshini kur ta boni commit !!!!!!

  container: {
    backgroundImage: `url(${ubtImage})`,
    backgroundPosition: 'center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    width: '100%',
    height: '846px',
    paddingTop: "100px",
},
paper:{
  paddingTop: theme.spacing(8),
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
},
form: {
  width: '100%',
  marginTop: theme.spacing(1),
},
submit: {
  margin: theme.spacing(3, 0, 2),
  fontSize: "18px",
  paddingTop: "18px",
  paddingBottom: "18px"
},
formControl: {
marginTop: theme.spacing(2),
minWidth: 'calc(100%)',
maxWidth: 300,
},
  }));

export default Login;
