import React from 'react';
import { GrLocation } from "react-icons/gr";
import { MdEmail } from "react-icons/md";
import { BsPhone } from "react-icons/bs"
import emailjs from '@emailjs/browser';


function Footer() {
    const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <footer className="page-footer font-small blue pt-4">
    <div className="container-fluid text-center text-md-left">
        <div className="row">

            <hr className="clearfix w-100 d-md-none pb-0"/>

            <div className="col-md-5 text-center py-3">
                <ul className="list-unstyled">
               
                <h6><GrLocation/>Lagjja Spitalit, Prishtine 10000, Kosove</h6>
                <a className="btn btn-outline-primary btn-xs" href="/register">Gjej rrugen</a>
                </ul>

                <ul className="list-unstyled">
                <h6><MdEmail/>drejtoria.shskuk@rks-gov.net</h6>
                </ul>

                <ul className="list-unstyled">
                <h6><BsPhone/>+381 38 500 600</h6>
                </ul>
            </div>

            <div className="col-md-3 mb-md-0 mb-3">
                <h5 className="text-uppercase">Vlereso sistemin</h5>
                <ul className="list-unstyled">
                <form ref={form} onSubmit={sendEmail}>
      <label>Name</label>
      <input type="text" name="user_name" />
      <label>Email</label>
      <input type="email" name="user_email" />
      <label>Message</label>
      <textarea name="message" />
      <input type="submit" value="Send" />
    </form>
                </ul>
            </div>
        </div>
    </div>

    <div className="footer-copyright text-center py-3">Â© 2020 Copyright: QKUK
    
    </div>

</footer>

  )
}

export default Footer
